import React from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';

const imageFiles = [
  '/images/Carousel/photo1.jpg',
  '/images/Carousel/photo2.jpg'
];

function ChurchGallery() {
  return (
    <section className="p-8 bg-purple-50 text-purple-900">
      <h2 className="text-2xl font-bold mb-4 text-center">Church Gallery</h2>
      <div className="mx-auto max-w-4xl overflow-hidden rounded-xl shadow-lg" style={{ height: '500px' }}>
        <Carousel
          autoPlay
          infiniteLoop
          interval={4000}
          showThumbs={false}
          showStatus={false}
          animationHandler="fade"
          swipeable={false}
          emulateTouch={false}
        >
          {imageFiles.map((src, index) => (
            <div key={index} className="h-full w-full">
              <img
                src={src}
                alt={`Slide ${index + 1}`}
                className="w-full h-full object-cover"
                style={{ height: '500px' }}
              />
            </div>
          ))}
        </Carousel>
      </div>
    </section>
  );
}

export default ChurchGallery;
